mapper.select:
SELECT user_id AS userId, email, username, password, hp, affiliation, is_active AS isActive, role, join_at AS joinAt, updated_at AS updatedAt
FROM user
WHERE email = #{email}

SELECT user_id AS userId, email, username, password, hp, affiliation, is_active AS isActive, role, join_at AS joinAt, updated_at AS updatedAt
FROM user
WHERE email = #{email} AND password = #{password}
